mapper.select:
select 
A.*
from web_product_group_links as A left join web_product_groups as B on A.group_id=B.group_id where B.tag=#{tag,jdbcType=VARCHAR}

mapper.insert:
insert into web_product_group_links (group_id, product_id, position
  )
values (#{groupId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, #{position,jdbcType=INTEGER}
  )
